import axios from "axios";
import {useEffect, useState} from "react";
import * as studentService from "../../services/StudentService"
import {useDispatch, useSelector} from "react-redux";
import {getAllStudent} from "../../redux/middleware/StudentMiddleware";

function StudentList() {
    const students = useSelector(store => store.students);
    const [nameSearch, setNameSearch] = useState("");
    const dispatch = useDispatch();

    useEffect( () => {
        //call api

        dispatch(getAllStudent(nameSearch));

    }, [nameSearch]);

    // const getAllStudent = async (nameSearch) => {
    //     try {
    //         let temp = await studentService.findAll();
    //         setStudents(temp);
    //     } catch (e) {
    //         console.log(e);
    //     }
    // }

    // useEffect(() => {
    //     //call api search
    //     console.log("1")
    // }, [nameSearch])

    return (
        <>

            <input onChange={(evt) => setNameSearch(evt.target.value)}/>
            <h1>Danh sách học sinh lớp </h1>
            <table width="500px">
                <thead>
                <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Age</th>
                    <th>Gender</th>
                    <th>Languages</th>
                </tr>
                </thead>
                <tbody>
                {students?.map((item, index) => (
                        <tr key={item.id}>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>{item.age}</td>
                            <td>{item.gender}</td>
                            <td>{item.languages}</td>
                        </tr>
                    )
                )}
                </tbody>
            </table>
        </>
    )
}

export default StudentList;
